        ff:EligibilityConstraint ;
    sh:property [ sh:description "The person must be between 18 and 67 years old." ;
            sh:maxInclusive 67 ;
            sh:minInclusive 18 ;
            sh:path ff:hasAge ],
        [ sh:datatype xsd:decimal ;
            sh:description "The person must not earn more than 1500 euros per month." ;
            sh:maxInclusive 1500 ;
            sh:path ff:einkommen_neu ],
        [ sh:datatype xsd:decimal ;
            sh:description "The person's income must be a numerical value." ;
            sh:maxInclusive 1500 ;
            sh:minInclusive 0 ;
            sh:path ff:incomeValue ] ;
    sh:targetClass ff:Citizen .

