feedback_history.improved_shape:
@prefix ff: <https://foerderfunke.org/default#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

ff:buergergeld a ff:RequirementProfile ;
    ff:hasMainPersonShape ff:buergergeldMainPersonShape .

ff:buergergeldMainPersonShape a sh:NodeShape,
        ff:EligibilityConstraint ;
    sh:property [ sh:description "The person must be between 18 and 67 years old." ;
            sh:maxInclusive 67 ;
            sh:minInclusive 18 ;
            sh:path ff:hasAge ],
        [ sh:datatype xsd:decimal ;
            sh:description "The person must not earn more than 1500 euros per month." ;
            sh:maxInclusive 1500 ;
            sh:path ff:einkommen_neu ],
        [ sh:datatype xsd:decimal ;
            sh:description "The person's income must be a numerical value." ;
            sh:maxInclusive 1500 ;
            sh:minInclusive 0 ;
            sh:path ff:incomeValue ] ;
    sh:targetClass ff:Citizen .

@prefix ff: <https://foerderfunke.org/default#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

ff:buergergeld a ff:RequirementProfile ;
    ff:hasMainPersonShape ff:buergergeldMainPersonShape .

ff:buergergeldMainPersonShape a sh:NodeShape,
        ff:EligibilityConstraint ;
    sh:property [ sh:description "The person must be between 18 and 67 years old." ;
            sh:maxInclusive 67 ;
            sh:minInclusive 18 ;
            sh:path ff:hasAge ],
        [ sh:description "The person must be fit for work." ;
            sh:in ( ff:erwerbsfaehig_neu-ao-ja ) ;
            sh:path ff:erwerbsfaehig_neu ],
        [ sh:datatype xsd:decimal ;
            sh:description "The person must not earn more than 1500 euros per month." ;
            sh:maxInclusive 1500 ;
            sh:path ff:incomeValue ] ;
    sh:targetClass ff:Citizen .
